mapper.select:
SELECT 	DISTINCT stockCode
FROM 	stockDaily
WHERE   tradeDate between #{startDate} AND #{endDate}

SELECT  ROUND( SQRT( SUM( POW( priceFinal - #{priceAverage}, 2 ) ) / #{dataCount} ), 0 ) priceStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

SELECT  ROUND( SQRT( SUM( POW( volumeTrade - #{volumeAverage}, 2 ) ) / #{dataCount} ), 0 ) volumeStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

SELECT  ROUND( SQRT( SUM( POW( foreignerStockCount - #{foreignerAverage}, 2 ) ) / #{dataCount} ), 0 ) foreignerStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

mapper.update:
UPDATE stockDaily SET
isin = #{isin}, stockName = #{stockName}, priceFinal = #{priceFinal}, priceChange = #{priceChange}, priceStart = #{priceStart}, priceHigh = #{priceHigh}, priceLow = #{priceLow}, priceTrade = #{priceTrade}, volumeTrade = #{volumeTrade}, priceTotal = #{priceTotal}, volumeTotal = #{volumeTotal}, rateChange = #{rateChange}, volumeOrg = #{volumeOrg}, volumeForeigner = #{volumeForeigner}, foreignerStockCount = #{foreignerStockCount}, foreignerHoldRate = #{foreignerHoldRate}, updateTime = now()
WHERE stockDaily.stockCode=#{stockCode} AND stockDaily.tradeDate=#{tradeDate}
