mapper.select:
SELECT  ROUND( SQRT( SUM( POW( priceFinal - #{priceAverage}, 2 ) ) / #{dataCount} ), 0 ) priceStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

SELECT  ROUND( SQRT( SUM( POW( volumeTrade - #{volumeAverage}, 2 ) ) / #{dataCount} ), 0 ) volumeStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

SELECT  ROUND( SQRT( SUM( POW( foreignerStockCount - #{foreignerAverage}, 2 ) ) / #{dataCount} ), 0 ) foreignerStandardDeviation
FROM    stockDaily
WHERE   tradeDate BETWEEN #{startDate} AND #{endDate} AND stockCode=#{stockCode}

mapper.update:
UPDATE stockDaily SET
isin = #{isin}, marketCode = #{marketCode}, stockName = #{stockName}, pricePrevious = #{pricePrevious}, priceFinal = #{priceFinal}, priceChange = #{priceChange}, priceStart = #{priceStart}, priceHigh = #{priceHigh}, priceLow = #{priceLow}, priceTrade = #{priceTrade}, volumeTrade = #{volumeTrade}, priceTotal = #{priceTotal}, volumeTotal = #{volumeTotal}, volumeTotalPrevious = #{volumeTotalPrevious}, rateChange = #{rateChange}, volumeOrg = #{volumeOrg}, volumeForeigner = #{volumeForeigner}, foreignerStockCount = #{foreignerStockCount}, foreignerHoldRate = #{foreignerHoldRate}, updateTime = now()
WHERE stockDaily.stockCode=#{stockCode} AND stockDaily.tradeDate=#{tradeDate}
